mapper.select:
select id, name, littleTitle, title, childCategoryId, addDate, weight, isDelete
from category
where category.id = #{id}

mapper.update:
SET FOREIGN_KEY_CHECKS = 0;
replace into category set
id=#{id}, name=#{name}, littleTitle=#{littleTitle}, title=#{title}, childCategoryId=#{childCategoryId}, addDate=#{addDate}, weight=#{weight}, isDelete=#{isDelete};
SET FOREIGN_KEY_CHECKS = 1;

SET FOREIGN_KEY_CHECKS = 0;

replace into category set id=#{category.id}, name=#{Category.name}, littleTitle=#{Category.littleTitle}, title=#{Category.title}, childCategoryId=#{Category.childCategoryId}, addDate=#{Category.addDate}, weight=#{Category.weight}, isDelete=#{Category.isDelete};

SET FOREIGN_KEY_CHECKS = 1;
