mapper.select:
select id, title, conect
from contact
where contact.id = #{id}

mapper.update:
SET FOREIGN_KEY_CHECKS = 0;
replace into contact set
id=#{id}, title=#{title}, conect=#{conect};
SET FOREIGN_KEY_CHECKS = 1;

SET FOREIGN_KEY_CHECKS = 0;

replace into contact set id=#{contact.id}, title=#{Contact.title}, conect=#{Contact.conect};

SET FOREIGN_KEY_CHECKS = 1;
