mapper.select:
select id, title, fdescribe, price, categoryId, imgId, isDelete, isHot, weight, addDate
from furniture
where furniture.id = #{id}

mapper.update:
SET FOREIGN_KEY_CHECKS = 0;
replace into furniture set
id=#{id}, title=#{title}, fdescribe=#{fdescribe}, price=#{price}, categoryId=#{categoryId}, imgId=#{imgId}, isDelete=#{isDelete}, isHot=#{isHot}, weight=#{weight}, addDate=#{addDate};
SET FOREIGN_KEY_CHECKS = 1;

SET FOREIGN_KEY_CHECKS = 0;

replace into furniture set id=#{furniture.id}, title=#{Furniture.title}, fdescribe=#{Furniture.fdescribe}, price=#{Furniture.price}, categoryId=#{Furniture.categoryId}, imgId=#{Furniture.imgId}, isDelete=#{Furniture.isDelete}, isHot=#{Furniture.isHot}, weight=#{Furniture.weight}, addDate=#{Furniture.addDate};

SET FOREIGN_KEY_CHECKS = 1;
