mapper.select:
select id, name, furnitureId, addDate
from img
where img.id = #{id}

mapper.update:
SET FOREIGN_KEY_CHECKS = 0;
replace into img set
id=#{id}, name=#{name}, furnitureId=#{furnitureId}, addDate=#{addDate};
SET FOREIGN_KEY_CHECKS = 1;

SET FOREIGN_KEY_CHECKS = 0;

replace into img set id=#{img.id}, name=#{Img.name}, furnitureId=#{Img.furnitureId}, addDate=#{Img.addDate};

SET FOREIGN_KEY_CHECKS = 1;
